mapper.select:
select * from tb_Student t


        t.username like CONCAT('%',#{ vo.username },'%');


        t.name like CONCAT('%',#{ vo.name },'%');


        t.gender like CONCAT('%',#{ vo.gender },'%');


        t.numb like CONCAT('%',#{ vo.numb },'%');


        t.clazzid like CONCAT('%',#{ vo.clazzid },'%');

mapper.update:
update tb_Student
set id=#{id}  ,username=#{username}  ,password=#{password}  ,name=#{name}  ,avatar=#{avatar}  ,gender=#{gender}  ,age=#{age}  ,tele=#{tele}  ,place=#{place}  ,ruxsj=#{ruxsj}  ,numb=#{numb}  ,clazzid=#{clazzid} 
where id= #{id}
